Example.ExampleCode:
int[] initValues = new int[] { 1, 2, 3, 4, 5 };
var qry = initValues
    .AsQueryable()
    .Select(x => new { strValue = "str", intValue = x })
    .GroupBy(x => x.strValue);

var result = qry.Select("Sum(intValue)");

Example.EquivalentCode:
int[] initValues = new int[] { 1, 2, 3, 4, 5 };
var qry = initValues
    .AsQueryable()
    .Select(x => new { strValue = "str", intValue = x })
    .GroupBy(x => x.strValue);

var result = qry.Sum(x => x.intValue);

Example.ExampleCode:
public class SimpleValuesModel
{
    public float FloatValue { get; set; }

    public decimal DecimalValue { get; set; }
}

var models = new SimpleValuesModel[] { 
    new SimpleValuesModel() { 
        FloatValue = 2, 
        DecimalValue = 3 
        } 
    }.AsQueryable();

var result = models
    .Select("Decimal(FloatValue) * DecimalValue").First();

Example.EquivalentCode:
public class SimpleValuesModel
{
    public float FloatValue { get; set; }

    public decimal DecimalValue { get; set; }
}

var models = new SimpleValuesModel[] { 
    new SimpleValuesModel() { 
        FloatValue = 2, 
        DecimalValue = 3 
        } 
    }.AsQueryable();

var result = models
    .Select(x => (Decimal)x.FloatValue * DecimalValue).First();
